import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Iproducts } from 'src/app/interface/products';
import { ProductsService } from 'src/app/service/products.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.scss']
})
export class ProductsComponent {
    products!: Iproducts[];
    // category!:any;

    constructor(private productsServer:ProductsService,
      private route: ActivatedRoute,
      private routers: Router){
        this.productsServer.getAllProducts().subscribe((data))
        // this.productsServer.getCategory().subscribe(data=>{
        //   this.category = data
        // })
      }
}

<div class="relative mt-5 overflow-x-auto shadow-md sm:rounded-lg">
    <th scope="col" class="add_products w-full text-sm text-left text-gray-500 dark:text-gray-400 px-6 py-3">
        <a routerLink = 'add'>Add Category</a>
    </th>
    <table class="w-full text-sm text-left text-gray-500 dark:text-gray-400">
        <thead class="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
            <tr>
                <th scope="col" class="px-6 py-3">
                    name
                </th>
                <th scope="col" class="px-6 py-3">
                    images
                </th>
                <th scope="col" class="px-6 py-3">
                    Price
                </th>
                <th scope="col" class="px-6 py-3">
                    Category
                </th>
                <th scope="col" class="px-6 py-3">
                    Action
                </th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let item of products" 
            class="bg-white border-b dark:bg-gray-900 dark:border-gray-700">
                <th scope="row" class="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                    {{item.name}}
                </th>
                <td class="px-6 py-4">
                    <img [src]="item.images" alt="">
                </td>
                <td class="px-6 py-4">
                    {{item.price}}
                </td>
                <td class="px-6 py-4">
                    $2999
                </td>
                <td class="px-6 py-4">
                    <a href="#" class="font-medium text-blue-600 dark:text-blue-500 hover:underline">Edit</a>
                </td>
            </tr>
        </tbody>
    </table>
</div>
