import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ICategory } from 'src/app/interface/category';
import { CategoryService } from 'src/app/service/category.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.scss']
})
export class CategoryComponent {
  categorys!: ICategory[];


  constructor(private category: CategoryService,
    private route: ActivatedRoute,
    private routers: Router) {
    this.category.getAllCategory().subscribe((response: any) => {
      this.categorys = response.data
      console.log(response.data);

    })
  }

  removeId(_id:any){
      this.category.deleteCategory(_id).subscribe((data)=>{
        Swal.fire({
          title: 'Xác nhận xóa',
          text: 'Bạn có chắc chắn muốn xóa Danh mục?',
          icon: 'warning',
          showCancelButton: true,
          confirmButtonText: 'OK',
          cancelButtonText: 'Hủy',
          reverseButtons: true
        }).then((result) => {
          if (result.isConfirmed) {
            Swal.fire({
              position: 'center',
              icon: 'success',
              title: 'Xóa danh mục thành công',
              text: 'Danh mục đã được xóa thành công!',
              showConfirmButton: false,
              timer: 2000,
              iconHtml: '<i class="fas fa-check-circle"></i>'
            });
            this.categorys = this.categorys.filter(item=>item._id !== _id)
          } else if (result.dismiss === Swal.DismissReason.cancel) {
            Swal.fire(
              'Hủy bỏ',
              'Dữ liệu không bị xóa.',
              'info'
            );
          }
        });
      })
  }
}

<div class="relative mt-5 overflow-x-auto shadow-md sm:rounded-lg">

    <th scope="col" class="add_category w-full text-sm text-left text-gray-500 dark:text-gray-400 px-6 py-3">
        <a routerLink = 'add'>Add Category</a>
    </th>
    <table class="w-full text-sm text-left text-gray-500 dark:text-gray-400">
        <thead class="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
            <tr>
                <th scope="col" class="px-6 py-3">
                    name category
                </th>
                <th scope="col" class="px-6 py-3">
                    Action
                </th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let cate of categorys"
            class="bg-white border-b dark:bg-gray-900 dark:border-gray-700">
                <th scope="row" class="category_item px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                    {{cate.name}}
                </th>
                <td class="px-6 py-4">
                    <button class="font-medium text-red-500 dark:text-blue-500 hover:underline" (click) ="removeId(cate._id!)">Edit</button>
                    <a [routerLink]="[cate._id,'edit']" class="m-6 ms-5 font-medium text-blue-600 dark:text-blue-500 hover:underline">Update</a>
                </td>
            </tr>
        </tbody>
    </table>
</div>






<form>
    <div class="flex">
