import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { IProducts } from 'src/app/interface/products';
import { ProductsService } from 'src/app/service/products.service';

@Component({
  selector: 'app-pages-detail',
  templateUrl: './pages-detail.component.html',
  styleUrls: ['./pages-detail.component.scss']
})
export class PagesDetailComponent {
  products!: IProducts;
  categoryName!: string;
  similarProducts: IProducts[] = [];

  constructor(
    private router: ActivatedRoute,
    private productsService: ProductsService
  ) {
    const id = this.router.snapshot.paramMap.get('id');
    if (id) {
      this.productsService.getProduct(id).subscribe((data: any) => {
        this.products = data.products;
        this.categoryName = data.categoryId.name;
        this.loadSimilarProducts(data.categoryId._id);
      });
    }
  }

  loadSimilarProducts(categoryId: string) {
    this.productsService.getSimilarProducts(categoryId).subscribe((data: IProducts[]) => {
      this.similarProducts = data;
    });
  }

  formatCurrency(value: number): string {
    const formatter = new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND',
      minimumFractionDigits: 0
    });

    return formatter.format(value);
  }
}
