import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { IProducts } from 'src/app/interface/products';
import { ProductsService } from 'src/app/service/products.service';

@Component({
  selector: 'app-layout-user',
  templateUrl: './layout-user.component.html',
  styleUrls: ['./layout-user.component.scss']
})
export class LayoutUserComponent {
  searchValue: any;
  isShown: boolean = true
  products!: IProducts[]
  constructor(private router:Router,
    private productService: ProductsService,
    private route: ActivatedRoute){}


    onSearch() {
      console.log(`product:`, this.searchValue)
      this.isShown = true;
      this.productService.getAllProducts().subscribe((response: any) => {
        this.products = response.products.filter((product: any) => {
          return product.name.toLowerCase().includes(this.searchValue == "" ? null : this.searchValue.toLowerCase())
        })
      })

    }
    ngOnInit() {
      this.onSearch();
    }

    onClickOutside() {
      this.isShown = false;
    }

    onClick(item: IProducts) {
      this.isShown = !this.isShown;
      this.router.navigate(['/pages-detail', item._id]).then(() => {
        window.location.reload();
      });
    }
}
