import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ICategory } from 'src/app/interface/category';
import { CategoryService } from 'src/app/service/category.service';
import { ProductsService } from 'src/app/service/products.service';

@Component({
  selector: 'app-test',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.scss']
})
export class TestComponent {
  category!:ICategory;
  products!:any;
  test: any[] = []

 constructor(
   private router: ActivatedRoute,
   private categoryService: CategoryService,
   private productService: ProductsService
 ) {
   const id = this.router.snapshot.paramMap.get('id')!;
   console.log(id);

   this.categoryService.getproductByCategory(id).subscribe((response:any) => {
     this.products = response;
     console.log(response.products);

     // for (let _id of this.products.products) {
     //   this.productService.getProduct(id).subscribe((response: any) => {
     //     this.test.push(response.data);
     //   });
     // }

   });
   this.categoryService.getAllCategory().subscribe((response:any) => {
     this.category = response.data;
     console.log(this.category);
   });
 }


 formatCurrency(value: number): string {
   const formatter = new Intl.NumberFormat('vi-VN', {
     style: 'currency',
     currency: 'VND',
     minimumFractionDigits: 0
   });

   return formatter.format(value);
 }
}
