import { Component } from '@angular/core';
import {FormBuilder,Validators} from '@angular/forms'
import { ICategory } from 'src/app/interface/category';
import { CategoryService } from 'src/app/service/category.service';

@Component({
  selector: 'app-catedory-update',
  templateUrl: './catedory-update.component.html',
  styleUrls: ['./catedory-update.component.scss']
})
export class CatedoryUpdateComponent {
  submitValue: boolean = false;
  category!: ICategory[];

  categoryForm = this.formBuilder.group({
    name: ['', [Validators.required]]
  })

  constructor(private formBuilder: FormBuilder,
    private cate: CategoryService,
    private routers: Router) { }

  onhandledSubmit() {
    this.submitValue = true
    if (this.categoryForm.valid) {
      const category: ICategory = {
        name: this.categoryForm.value.name || ''
      }
      if (confirm('Bạn có muốn thêm sản phẩm này ko!')) {
        this.cate.categoryAdd(category).subscribe((category) => {
          this.routers.navigate(['admin/category'])
        })
        setTimeout(() => {
          alert('Thêm danh mục thành công!')
        }, 3000);
      }
    }
  }
}
