import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

import { ICategory } from '../interface/category';

@Injectable({
  providedIn: 'root'
})
export class ProductsService {

  private API = 'http://localhost:8080/api';
  // token = localStorage.getItem('token')
  // httpOptions = {
  //   headers: new HttpHeaders({
  //     'Content-Type': 'application/json',
  //     // Authorization: 'Bearer ' + this.token,
  //     Authorization: 'Bearer ' ,
  //   }),
  // }
  constructor(
    private http: HttpClient
  ) { }

  getAllProducts(): Observable<I[]>{
    const url = `${this.API}/products`;
    return this.http.get<I[]>(url)
  }

  getProduct(_id: string | null): Observable<I> {
    const url = `${this.API}/products/${_id}`;
    return this.http.get<I>(url)
  }

  addProduct(products: I):Observable<I>{
    const url = `${this.API}/products`;
    return this.http.post<I>(url,products)
  }

  getCategory(): Observable<ICategory> {
    const url = `${this.API}/category`;
    return this.http.get<ICategory>(url)
  }

  updateProduct(products: I): Observable<I> {
    const url = `${this.API}/products/${products._id}`;
    return this.http.patch<I>(url, products);
  }

  deleteProduct(id: string | null): Observable<I> {
    const url = `${this.API}/products/${id}`;
    return this.http.delete<I>(url)
  }
}
