import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { IProduct } from 'src/app/interfaces/product';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-pages-detail',
  templateUrl: './pages-detail.component.html',
  styleUrls: ['./pages-detail.component.scss']
})
export class PagesDetailComponent implements OnInit {
  product!: IProduct;
  categoryName!: string;
  similarProducts: IProduct[] = [];

  constructor(
    private route: ActivatedRoute,
    private productService: ProductService
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.productService.getProduct(id).subscribe((data: any) => {
        this.product = data;
        console.log(data);

        // Lấy sản phẩm tương tự theo ID danh mục
        const categoryId = data.categoryId;
        if (categoryId) {
          this.getSimilarProducts(categoryId);
        }
      });
    }
  }

  formatCurrency(value: number): string {
    const formatter = new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND',
      minimumFractionDigits: 0
    });

    return formatter.format(value);
  }

  getSimilarProducts(categoryId: any): void {
    this.productService.getProductsByCategory(categoryId).subscribe((data: IProduct[]) => {
      this.similarProducts = data;
    });
  }
}
